import androidx.lifecycle.MutableLiveData
import com.example.recetasdecocina.model.IngredientItem
import com.example.recetasdecocina.model.Recipe
import java.util.UUID

// Creamos un objeto Singleton para el repositorio, para que solo exista una instancia
object RecipeRepository {

    // MutableLiveData para observar los cambios en la lista de recetas
    private val _recipes = MutableLiveData<MutableList<Recipe>>(mutableListOf())
    val recipes: LiveData<MutableList<Recipe>> = _recipes // Esta propiedad ya tiene un getter implícito

    // Datos de ejemplo para empezar
    init {
        // Datos de ejemplo de recetas
        val sampleRecipes = mutableListOf(
            Recipe(
                name = "Ensalada César Clásica",
                description = "Una ensalada refrescante con lechuga romana, crutones, queso parmesano y aderezo César.",
