                prepTimeMinutes = 15,
                cookTimeMinutes = 0,
                servings = 2,
                imageUrl = "data:image/jpeg;base64,",
                ingredients = mutableListOf(
                    IngredientItem("Lechuga romana", "1", "cabeza"),
                    IngredientItem("Pan", "2", "rebanadas (para crutones)"),
                    IngredientItem("Queso Parmesano", "50", "g"),
                    IngredientItem("Aderezo César", "4", "cucharadas")
                )
            ),
            Recipe(
                name = "Pasta Arrabiata Picante",
                description = "Pasta con una salsa de tomate picante, ajo y guindilla.",
                instructions = "Cocina la pasta. Saltea ajo y guindilla. Añade tomate triturado y cocina a fuego lento. Mezcla con la pasta.",
                prepTimeMinutes = 10,
                cookTimeMinutes = 20,
                servings = 4,
                imageUrl = "data:image/jpeg;base64,",
                ingredients = mutableListOf(
                    IngredientItem("Pasta Penne", "300", "g"),
                    IngredientItem("Tomate triturado", "400", "g"),
                    IngredientItem("Ajo", "3", "dientes"),
                    IngredientItem("Guindilla", "1", "unidad"),
                    IngredientItem("Aceite de oliva", "2", "cucharadas")
                )
            ),
            Recipe(
                name = "Tacos de Pollo al Pastor",
                description = "Deliciosos tacos inspirados en el sabor al pastor, con pollo marinado y piña.",
                instructions = "Marina el pollo. Cocínalo y pícalo. Calienta tortillas y arma los tacos con piña y cilantro.",
                prepTimeMinutes = 25,
                cookTimeMinutes = 30,
                servings = 3,
                imageUrl = "data:image/jpeg;base64,",
                ingredients = mutableListOf(
                    IngredientItem("Pechuga de pollo", "500", "g"),
                    IngredientItem("Tortillas de maíz", "12", "unidades"),
                    IngredientItem("Piña", "1/4", "pieza"),
                    IngredientItem("Cilantro", "al gusto", "ramo"),
